def calculate_rsi(prices, period=14):
    """Calculate RSI (Relative Strength Index)"""
    if len(prices) < period + 1:
        return None
    
    deltas = np.diff(prices)
    gains = np.where(deltas > 0, deltas, 0)
    losses = np.where(deltas < 0, -deltas, 0)
    
    avg_gain = np.mean(gains[:period])
    avg_loss = np.mean(losses[:period])
    
    for i in range(period, len(deltas)):
        avg_gain = (avg_gain * (period - 1) + gains[i]) / period
        avg_loss = (avg_loss * (period - 1) + losses[i]) / period
    
    if avg_loss == 0:
        return 100
    
    rs = avg_gain / avg_loss
    rsi = 100 - (100 / (1 + rs))
    return rsi

def calculate_macd(prices, fast=12, slow=26, signal=9):
    """Calculate MACD (Moving Average Convergence Divergence)"""
    if len(prices) < slow:
        return None, None, None
    
    # Calculate EMAs
    ema_fast = pd.Series(prices).ewm(span=fast).mean()
    ema_slow = pd.Series(prices).ewm(span=slow).mean()
    
    # MACD line
    macd_line = ema_fast - ema_slow
    
    # Signal line
    signal_line = macd_line.ewm(span=signal).mean()
    
    # Histogram
    histogram = macd_line - signal_line
    
    return macd_line.iloc[-1], signal_line.iloc[-1], histogram.iloc[-1]

def calculate_bollinger_bands(prices, period=20, std_dev=2):
    """Calculate Bollinger Bands"""
    if len(prices) < period:
        return None, None, None
    
    sma = np.mean(prices[-period:])
    std = np.std(prices[-period:])
    
    upper_band = sma + (std * std_dev)
    lower_band = sma - (std * std_dev)
    
    return upper_band, sma, lower_band

def calculate_moving_averages(prices):
    """Calculate various moving averages"""
    ma_5 = np.mean(prices[-5:]) if len(prices) >= 5 else None
    ma_10 = np.mean(prices[-10:]) if len(prices) >= 10 else None
    ma_20 = np.mean(prices[-20:]) if len(prices) >= 20 else None
    ma_50 = np.mean(prices[-50:]) if len(prices) >= 50 else None
    
    return ma_5, ma_10, ma_20, ma_50

@lru_cache(maxsize=1000)
def get_comprehensive_stock_data(symbol, stoch_period=14, rsi_period=14):
    """
    Fetch stock data and calculate all technical indicators
    """
    try:
        # Get historical data for indicator calculations
        url = f"https://query1.finance.yahoo.com/v8/finance/chart/{symbol}?interval=1d&range=3mo"
        headers = {"User-Agent": "Mozilla/5.0"}
        response = requests.get(url, headers=headers, timeout=10)
        data = response.json()
        
        result = data['chart']['result'][0]
        meta = result['meta']
        
        # Get OHLC data
        quotes = result['indicators']['quote'][0]
        timestamps = result['timestamp']
        
        high_prices = [h for h in quotes['high'] if h is not None]
        low_prices = [l for l in quotes['low'] if l is not None]
        close_prices = [c for c in quotes['close'] if c is not None]
        volumes = [v for v in quotes['volume'] if v is not None]
        
        if len(close_prices) < max(stoch_period, rsi_period, 20):
            return None
        
        # Basic price info
        current_price = meta['regularMarketPrice']
        previous_close = meta['chartPreviousClose']
        change = current_price - previous_close
        change_percent = (change / previous_close) * 100
        
        # Calculate 1-day price change (yesterday's close vs today's price)
        yesterday_close = close_prices[-2] if len(close_prices) > 1 else previous_close
        one_day_change = current_price - yesterday_close
        one_day_change_percent = (one_day_change / yesterday_close) * 100
        
        # Calculate Stochastic %K
        current_close = close_prices[-1]
        highest_high = max(high_prices[-stoch_period:])
        lowest_low = min(low_prices[-stoch_period:])
        
        if highest_high == lowest_low:
            stochastic_k = 50
        else:
            stochastic_k = ((current_close - lowest_low) / (highest_high - lowest_low)) * 100
        
        # Calculate RSI
        rsi = calculate_rsi(close_prices, rsi_period)
        
        # Calculate MACD
        macd, signal, histogram = calculate_macd(close_prices)
        
        # Calculate Bollinger Bands
        bb_upper, bb_middle, bb_lower = calculate_bollinger_bands(close_prices)
        
        # Calculate Moving Averages
        ma_5, ma_10, ma_20, ma_50 = calculate_moving_averages(close_prices)
        
        # Calculate additional metrics
        avg_volume = np.mean(volumes[-20:]) if len(volumes) >= 20 else np.mean(volumes)
        current_volume = volumes[-1] if volumes else 0
        volume_ratio = current_volume / avg_volume if avg_volume > 0 else 0
        
        # Price position within Bollinger Bands
        if bb_upper and bb_lower:
            bb_position = ((current_price - bb_lower) / (bb_upper - bb_lower)) * 100
        else:
            bb_position = None
        
        # Trend analysis
        trend_5_20 = "Up" if ma_5 and ma_20 and ma_5 > ma_20 else "Down" if ma_5 and ma_20 else "Neutral"
        trend_20_50 = "Up" if ma_20 and ma_50 and ma_20 > ma_50 else "Down" if ma_20 and ma_50 else "Neutral"
        
        return {
            'Symbol': symbol,
            'Price': current_price,
            'Change': change,
            'Change %': change_percent,
            '1D Change': one_day_change,
            '1D Change %': one_day_change_percent,
            'Stochastic %K': stochastic_k,
            'RSI': rsi,
            'MACD': macd,
            'MACD Signal': signal,
            'MACD Histogram': histogram,
            'BB Upper': bb_upper,
            'BB Middle': bb_middle,
            'BB Lower': bb_lower,
            'BB Position %': bb_position,
            'MA 5': ma_5,
            'MA 10': ma_10,
            'MA 20': ma_20,
            'MA 50': ma_50,
            'Volume': current_volume,
            'Avg Volume': avg_volume,
            'Volume Ratio': volume_ratio,
            'Trend 5/20': trend_5_20,
            'Trend 20/50': trend_20_50,
            'High 52W': meta.get('fiftyTwoWeekHigh', 0),
            'Low 52W': meta.get('fiftyTwoWeekLow', 0),
            'Market Cap': meta.get('marketCap', 0),
            'Updated': datetime.now().strftime("%H:%M:%S")
        }
        
    except Exception as e:
        st.error(f"Error fetching data for {symbol}: {str(e)}")
        return None

def scan_all_stocks(stock_list, filters=None):
    """
    Scan ALL stocks first, then apply filters and sorting
    """
    all_stocks_data = []
    progress_bar = st.progress(0)
    status_text = st.empty()
    
    for i, symbol in enumerate(stock_list):
        status_text.text(f"Scanning {i+1}/{len(stock_list)}: {symbol}")
        
        stock_data = get_comprehensive_stock_data(symbol)
        if stock_data:
            all_stocks_data.append(stock_data)
        
        progress_bar.progress((i + 1) / len(stock_list))
        time.sleep(0.1)  # Rate limiting
    
    progress_bar.empty()
    status_text.empty()
    
    return all_stocks_data

def apply_filters(stock_data, filters):
    """Apply user-defined filters to stock data"""
    try:
        # Only positive change filter
        if filters['positive_only'] and stock_data['Change %'] <= 0:
            return False
        
        # Stochastic filter
        if not (filters['min_stochastic'] <= stock_data['Stochastic %K'] <= filters['max_stochastic']):
            return False
        
        # RSI filter
        if stock_data['RSI'] and not (filters['min_rsi'] <= stock_data['RSI'] <= filters['max_rsi']):
            return False
        
        # Change % filter
        if stock_data['Change %'] < filters['min_change_percent']:
            return False
        
        # Volume filter
        if stock_data['Volume Ratio'] < filters['min_volume_ratio']:
            return False
        
        # Market cap filter
        if stock_data['Market Cap'] < filters['min_market_cap']:
            return False
        
        return True
    except:
        return False

def filter_and_sort_stocks(all_stocks_data, filters, sort_by, ascending, target_count):
    """Filter stocks and sort by selected indicator"""
    # Apply filters
    filtered_stocks = []
    for stock in all_stocks_data:
        if apply_filters(stock, filters):
            filtered_stocks.append(stock)
    
    # Convert to DataFrame for sorting
    if filtered_stocks:
        df = pd.DataFrame(filtered_stocks)
        
        # Sort by selected indicator
        df = df.sort_values(sort_by, ascending=ascending)
        
        # Limit to target count
        df = df.head(target_count)
        
        return df.to_dict('records')
    
    return []

